import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';
import { TestBed } from '@angular/core/testing';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { IoService } from '../io.service';
import { Account } from '../models/account';
import { DrawService } from '../services/draw.service';
import { UserService } from '../services/user.service';
import { HomeComponent } from './home.component';
describe('HomeComponent', () => {
    let component;
    let fixture;
    let router;
    let userService;
    let drawService;
    let matDialog;
    let matSnackBar;
    let socketService;
    let account = new Account('yana', 'dsdsf@fsfef', 23, [], [], []);
    beforeEach(async () => {
        userService = jasmine.createSpyObj('UserService', ['getProfile', 'getIcon', 'saveUserName']);
        userService.getProfile = jasmine.createSpy().and.returnValue(new Account('yana', 'dsdsf@fsfef', 23, [], [], []));
        userService.getIcon = jasmine.createSpy().and.returnValue('base64_of_animation');
        await TestBed.configureTestingModule({
            declarations: [HomeComponent],
            providers: [
                {
                    provide: UserService,
                    useValue: userService
                },
                {
                    provide: Router,
                    useValue: router
                },
                {
                    provide: DrawService,
                    useValue: drawService
                },
                {
                    provide: MatDialog,
                    useValue: matDialog
                },
                {
                    provide: MatSnackBar,
                    useValue: matSnackBar
                },
                {
                    provide: IoService,
                    useValue: socketService,
                }
            ],
            schemas: [CUSTOM_ELEMENTS_SCHEMA],
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(HomeComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should change username', () => {
        userService.saveUserName = jasmine.createSpy('changedName');
        component.username = 'newChanged';
        component.isSubmitted = true; //Копка підтвердження змін натиснута
        component.form.setValue({ email: 'new@gmail.com' });
    });
});
